/* gps_getPageGpsProductForSync */ select m3.* from (select m2.*, rownum rn from( select p.id id, p.product_code, p.product_cname, p.product_ename, p.product_sale_type, p.product_list_price, p.product_type, p.category_id, c.category_search_name, p.is_deleted, p.create_time, p.modi_date as from product p left join category c on p.category_id = c.id and c.IS_DELETE = 0 where p.IS_DELETED = 0 and p.IS_PERMANENT_OFFSALE = 0 and ((p.modi_date >= to_date(#startTime#,'YYYY-MM-DD HH24:MI:SS') and p.modi_date <= to_date(#endTime#,'YYYY-MM-DD HH24:MI:SS')) or (p.modi_date is null and p.create_time >= to_date(#startTime#,'YYYY-MM-DD HH24:MI:SS') and p.create_time <= to_date(#endTime#,'YYYY-MM-DD HH24:MI:SS')) or (c.UPDATE_TIME >= to_date(#startTime#,'YYYY-MM-DD HH24:MI:SS') and c.UPDATE_TIME <= to_date(#endTime#,'YYYY-MM-DD HH24:MI:SS')))
and p.id > #lastId#
order by p.id ) m2 where rownum < #endRow#) m3 where rn >= #startRow#
